import React from "react";

import "./style.scss";

import Weather from "./weather"

import { usePopper } from 'react-popper';

export default class TopSection extends React.Component {

    constructor(props)
    {
        super(props);
        this.state = {};
    }

    render() {
        return (
          <div className="top-container">
            <div className="title">Weather App</div>
            <Weather {...this.props} />
            <button className="btn btn-select-location">Select Location</button>

            <Manager>
          <Reference>
            {({ ref }) => (
              <button
                className="btn btn-select-location"
                ref={ref}
                onClick={this.onToggleSelectLocation.bind(this)}
              >
                Select Location
              </button>
            )}
          </Reference>
          <Popper placement="top">
            {({ ref, style, placement, arrowProps }) =>
              isSelectLocationOpen && (
                <div
                  className="popup-container"
                  ref={ref}
                  style={style}
                  data-placement={placement}
                >
                  <div className="form-container">
                    <label htmlFor="location-name">Location Name</label>
                    <input
                      id="location-name"
                      type="text"
                      placeholder="City Name"
                      onChange={this.onLocationNameChange.bind(this)}
                    />
                    <button
                      className="btn btn-select-location"
                      onClick={this.onSelectCity.bind(this)}
                    >
                      Select
                    </button>
                  </div>
                  <div ref={arrowProps.ref} style={arrowProps.style} />
                </div>
              )
            }
          </Popper>
        </Manager>
      </div>
        );
    }
    
}
