import logo from './logo.svg';
import './App.css';

import "./sass/app.scss";

import axios from "axios";


import TopSection from "./components/top/top";
import BottomSection from "./components/bottom/bottom";

const WEATHER_KEY = "2c2119e198f5ca7ff8622f9055ad2912";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: "London",
      forecastDays: 5,
    }
  }

  componentDidMount() {
    const { cityName, forecastDays } = this.state;

    const URL = `http://api.weatherstack.com/forecast
    ? access_key = ${WEATHER_KEY}
    & query = ${cityName} 
    & forecast_days = ${forecastDays}`;

